import React, { useState } from 'react';
import { MapContainer, TileLayer, Popup } from 'react-leaflet';
import './App.scss';

function App() {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal);
  };

  const center = [15.660466, 30.846844];

  if (modal) {
    document.body.classList.add('active-modal');
  } else {
    document.body.classList.remove('active-modal');
  }

  return (
    <>
      <button onClick={toggleModal} className="btn-modal x">
        2019
      </button>

      {modal && (
        <div className="modal">
          <div className="overlay" onClick={toggleModal}></div>
          <div className="modal-content x">
            <MapContainer center={center} zoom={10} style={{ height: '500px', width: '100%' }}>
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              <Popup position={center}>
                A popup for 2019
              </Popup>
            </MapContainer>
          </div>
        </div>
      )}

      {/* Repeat the above block for 2020 and 2021 with different classNames */}

    </>
  );
}

export default App;
