import React, {useState} from 'react';
import ReactMapGl from 'react-map-gl';
import './App.scss';

function App() {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
      setModal(!modal);
  };

  if (modal){
    document.body.classList.add('active-modal')
  } else{
    document.body.classList.remove('active-modal')
  }

  const [viewport, setViewport] = useState({
    latitude: 45.4211,
    longitude: -75.6903,
    width: "100vw",
    height: "100vh",
    zoom: 10
  });
  const [selectedPark, setSelectedPark] = useState(null);

  const useEffect = (() => {
    const listener = e => {
      if (e.key === "Escape") {
        setSelectedPark(null);
      }
    };
    window.addEventListener("keydown", listener);

    return () => {
      window.removeEventListener("keydown", listener);
    };
  }, []);

  return(
      <>
      <button onClick={toggleModal} className="btn-modal x">
          2019
      </button>

      {modal && (<div className="modal">
          <div className="overlay" onClick={toggleModal}>

          </div>
          <div className="modal-content x">
             <ReactMapGl>
              markers here
             </ReactMapGl>
              <button className='close-modal'
              onClick={toggleModal}>
                Close
              </button>

          </div>
          </div>

          )}

<button onClick={toggleModal} className="btn-modal y">
          2020
      </button>

      {modal && (<div className="modal">
          <div className="overlay" onClick={toggleModal}>

          </div>
          <div className="modal-content y">
              <h2>
              interactive map
              </h2>
              <button className='close-modal'
              onClick={toggleModal}>
                Close
              </button>

          </div>
          </div>

          )}

<button onClick={toggleModal} className="btn-modal z">
          2021
      </button>

      {modal && (<div className="modal">
          <div className="overlay" onClick={toggleModal}>

          </div>
          <div className="modal-content z">
              <h2>
              interactive map
              </h2>
              <button className='close-modal'
              onClick={toggleModal}>
                Close
              </button>

          </div>
          </div>

          )}


      </>
  );
}

export default App;
