import React from "react";
import { Carousel } from 'react-carousel-minimal';

function loadLocalImage(filePath) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        resolve(reader.result);
      };
      reader.onerror = (error) => {
        reject(error);
      };
      reader.readAsDataURL(new File(filePath));
    });
  }

export const CarouselItem = () => {

    const data = [
        {
            image: loadLocalImage("./forum1.6cce6ab7.jpg"),
            caption: "San Francisco"
          },
          {
            image: loadLocalImage("./images/scotland_isle_skye.jpg"),
            caption: "Scotland"
          },
          {
            image: loadLocalImage("./images/darjeeling_tea_gardens.jpg"),
            caption: "Darjeeling"
          }
      ];

      const captionStyle = {
        fontSize: '2em',
        fontWeight: 'bold',
      }
      const slideNumberStyle = {
        fontSize: '20px',
        fontWeight: 'bold',
      }
      return (
        <div className="App">
          <div style={{ textAlign: "center" }}>
            <h2>React Carousel Minimal</h2>
            <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
            <div style={{
              padding: "0 20px"
            }}>
              <Carousel
                data={data}
                time={2000}
                width="850px"
                height="500px"
                captionStyle={captionStyle}
                radius="10px"
                slideNumber={true}
                slideNumberStyle={slideNumberStyle}
                captionPosition="bottom"
                automatic={true}
                dots={true}
                pauseIconColor="white"
                pauseIconSize="40px"
                slideBackgroundColor="darkgrey"
                slideImageFit="cover"
                thumbnails={true}
                thumbnailWidth="100px"
                style={{
                  textAlign: "center",
                  maxWidth: "850px",
                  maxHeight: "500px",
                  margin: "40px auto",
                }}
              />
            </div>
          </div>
        </div>
      );

};
